import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Button } from './Button';
import { ReactComponent as Logo } from '../assets/theMet.svg';
import { ReactComponent as Close } from '../assets/close.svg';
import { ReactComponent as Menu } from '../assets/menu.svg';
import './Navbar.css';

const BREAKPOINT = 600;

const NAV_ITEMS = [
  { to: '/tour', label: 'Tour' },
  { to: '/art-collection', label: 'Art Collection' },
  { to: '/education', label: 'Education' },
];

function Navbar() {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [showDesktopButtons, setShowDesktopButtons] = useState(true);
  const [hideElement, setHideElement] = useState(false);

  useEffect(() => {
    const handleResize = () => {
      setShowDesktopButtons(window.innerWidth > BREAKPOINT);
      setHideElement(window.innerWidth < 700);
      if (window.innerWidth > BREAKPOINT) {
        setIsMobileMenuOpen(false); // Reset mobile menu when switching to desktop
      }
    };

    handleResize(); // Initial check
    window.addEventListener('resize', handleResize);

    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const closeMobileMenu = () => setIsMobileMenuOpen(false);

  return (
    <nav className="navbar" aria-label="Main navigation">
      <div className="navbar-container">
        <Link to="/" className="navbar-logo" onClick={closeMobileMenu}>
          <Logo className="logo" aria-label="The Metropolitan Museum of Art" />
        </Link>

        <ul className={`nav-menu ${isMobileMenuOpen ? 'active' : ''}`}>
          {NAV_ITEMS.map(({ to, label }) => (
            <li key={to} className="nav-item">
              <Link to={to} className="nav-links" onClick={closeMobileMenu}>
                {label}
              </Link>
            </li>
          ))}
          {hideElement && (
            <>
              <li className="nav-item mobile-only">
                <Link to="/sign-up" className="nav-links-mobile" onClick={closeMobileMenu}>
                  Sign Up
                </Link>
              </li>
              <li className="nav-item mobile-only">
                <Link to="/login" className="nav-links-mobile" onClick={closeMobileMenu}>
                  Login
                </Link>
              </li>

            </>
          )}
        </ul>
        <div className="nav-menu-tablet">
          {showDesktopButtons && (
            <div className="button-group">
              <Button buttonStyle="btn--nav" to="/sign-up">Sign Up</Button>
              <Button buttonStyle="btn--nav-outline" to="/login">Login</Button>
            </div>
          )}
          <button 
            className="menu-icon"
            onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
            aria-expanded={isMobileMenuOpen}
            aria-label={isMobileMenuOpen ? "Close menu" : "Open menu"}
          >
            {isMobileMenuOpen ? <Close aria-hidden="true" /> : <Menu aria-hidden="true" />}
          </button>
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
